class Solution(object):
	def candy1(self, ratings):
		"""
		:type ratings: List[int]
		:rtype: int
		O(n^2) -> TLE
		"""
		self.length = len(ratings)	
		nums = [1 for x in xrange(0, self.length)]
		for x in xrange(1, self.length):
			if ratings[x] > ratings[x-1] and nums[x] <= nums[x-1]:
				nums[x] = nums[x-1] + 1
			elif ratings[x] < ratings[x-1] and nums[x] >= nums[x-1]:
				nums[x-1] = nums[x] +1 
				for y in xrange(x-1, 0, -1):
					if ratings[y] < ratings[y-1] and nums[y] >= nums[y-1]:
						nums[y-1] = nums[y] +1 
					else:
						break
		#print nums
		return sum(nums)

	def candy(self, ratings):
		"""
		:type ratings: List[int]
		:rtype: int
		O(n)
		"""
		self.length = len(ratings)	
		if self.length == 0:
			return 0
		elif self.length == 1:
			return 1
		elif self.length == 2:
			if ratings[0] == ratings[1]:
				return 2
			else :
				return 3
		nums = [1 for x in xrange(0, self.length)]
		points = []
		left = 0
		right = 0
		num = 0
		for x in xrange(1, self.length):
			if ratings[x] > ratings[x-1]:
				if right != 0:
					points.append([left,right])
					num += 1
					left = 0
					right = 0
				left = left +1
			elif ratings[x] < ratings[x-1]:
				right = right +1
			elif ratings[x] == ratings[x-1]:
				points.append([left,right])
				left = 0
				right = 0
		points.append([left,right])
		sum = 0
		for [left,right] in points:
			sum += left*(left+1)/2
			sum += right*(right+1)/2
			sum += max(left,right)+1
		sum -= num
		return sum



